            "type": "PLUGIN",   // 步骤类型
            "config": {
                "plugin_name": "mysql_exporter",
                "plugin_version": "2.3",
                "config_templates": [
                    {
                        "name": "config.yaml",
                        "version": "2",
                    },
                    {
                        "name": "env.yaml",
                        "version": "2",
                    }
                ]
            },
            "params": {
                "port_range": "9102,10000-10005,20103,30000-30100",
